package com.servinglynk.hmis.warehouse.core.model.exception;/** * This exception is thrown when an unauthorized action occurs * 
 * @author Srinivasa Bora */@SuppressWarnings("serial")public class AccessDeniedException extends RuntimeException {	/**	 * Default exception message	 */	public static final String DEFAULT_MESSAGE = "Unauthorized to perform this action.";	public AccessDeniedException() {		super(DEFAULT_MESSAGE);	}	public AccessDeniedException(String message) {		super(message);	}	public AccessDeniedException(Throwable cause) {		super(DEFAULT_MESSAGE, cause);	}	public AccessDeniedException(String message, Throwable cause) {		super(message, cause);	}}