package com.servinglynk.hmis.warehouse.core.model.exception;@SuppressWarnings("serial")public class IllegalBusinessStateException extends RuntimeException {	/**	 * Default exception message	 */	public static final String DEFAULT_MESSAGE = "Cannot peform this operation, preconditions not met";	public IllegalBusinessStateException() {		super(DEFAULT_MESSAGE);	}	public IllegalBusinessStateException(String message) {		super(message);	}	public IllegalBusinessStateException(Throwable cause) {		super(DEFAULT_MESSAGE, cause);	}	public IllegalBusinessStateException(String message, Throwable cause) {		super(message, cause);	}}