package com.servinglynk.hmis.warehouse.core.model.exception;@SuppressWarnings("serial")public class InvalidSessionTokenException extends RuntimeException {	/**	 * Default exception message	 */	public static final String DEFAULT_MESSAGE = "invalid session token";		public InvalidSessionTokenException() {		super(DEFAULT_MESSAGE);	}	public InvalidSessionTokenException(String message) {		super(message);	}	public InvalidSessionTokenException(Throwable cause) {		super(DEFAULT_MESSAGE, cause);	}	public InvalidSessionTokenException(String message, Throwable cause) {		super(message, cause);	}}