package com.servinglynk.hmis.warehouse.core.model.exception;@SuppressWarnings("serial")public class MissingParameterException extends RuntimeException {	/**	 * Default exception message	 */	public static final String DEFAULT_MESSAGE = "missing parameter";	public MissingParameterException() {		super(DEFAULT_MESSAGE);	}	public MissingParameterException(String message) {		super(message);	}	public MissingParameterException(Throwable cause) {		super(DEFAULT_MESSAGE, cause);	}	public MissingParameterException(String message, Throwable cause) {		super(message, cause);	}}