package com.servinglynk.hmis.warehouse.core.web.interceptor;import com.servinglynk.hmis.warehouse.core.model.Session;import com.servinglynk.hmis.warehouse.core.model.exception.InvalidSessionTokenException;public interface SessionValidator {	/**	 * Validates a given sessionToken. Returns a {@link Session} if the	 * sessionToken is valid. Throws an exception otherwise.	 * 	 * @param sessionToken	 *            The token that identifies a {@link Session} uniquely.	 *            	 * @throws InvalidSessionTokenException	 *             if the given sessionToken is invalid.	 * 	 * @return {@link Session} identified by the given sessionToken.	 */	public Session validateSessionToken(String sessionToken) throws InvalidSessionTokenException;}