package com.servinglynk.hmis.warehouse.notification.persistence.dao;import java.util.List;import org.hibernate.criterion.DetachedCriteria;import org.hibernate.criterion.Restrictions;import com.servinglynk.hmis.warehouse.notification.persistence.dao.core.ParentDao;import com.servinglynk.hmis.warehouse.notification.persistence.entity.NotificationHeaderEntity;import com.servinglynk.hmis.warehouse.notification.persistence.entity.NotificationParamEntity;public class NotificationHeaderDao extends ParentDao implements INotificationHeaderDao{	public NotificationHeaderEntity create(NotificationHeaderEntity notificationHeaderEntity){		insert(notificationHeaderEntity);		return notificationHeaderEntity;	}	public NotificationHeaderEntity update(NotificationHeaderEntity notificationHeaderEntity){		return (NotificationHeaderEntity)update(notificationHeaderEntity);	}	public void delete(NotificationHeaderEntity notificationHeaderEntity){		delete(notificationHeaderEntity);	}	@SuppressWarnings("unchecked")	public List<NotificationHeaderEntity> read(NotificationHeaderEntity notificationHeaderEntity){		DetachedCriteria query = DetachedCriteria.forClass(NotificationHeaderEntity.class);		return (List<NotificationHeaderEntity>)findByCriteria(query);	}	@SuppressWarnings("unchecked")	public List<NotificationHeaderEntity> getNotificationHeaderByExternalId(String externalId){		DetachedCriteria query = DetachedCriteria.forClass(NotificationHeaderEntity.class);		return (List<NotificationHeaderEntity>)findByCriteria(query);	}		@SuppressWarnings("unchecked")	public NotificationParamEntity getNotificationParamByNotificationType(String externalId){		DetachedCriteria criteria=DetachedCriteria.forClass(NotificationParamEntity.class);		criteria.add(Restrictions.eq("externalId", externalId));		List<NotificationParamEntity> notificationParams = (List<NotificationParamEntity>) findByCriteria(criteria);		if(notificationParams.size()>0) return notificationParams.get(0);		return null;	}}